public class Ejercicio14 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Escribir un programa que visualice los múltiplos de 3 que están entre 0 y 20.
		
		System.out.println("Múltiplos de 3");
		for (int i = 0; i<=20;i++) {
			
			if (i %3 ==0) {
				System.out.print(i + " ");
			}
			
		}
		
		
		
	}

}
