package Ejercicio2_3;

public class Ejercicio3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*Escribir un programa que imprima los 10 primeros n√∫meros enteros empezando 
		 * por el cero, excepto 5. (Utilizando for y while)
		 */
		
		int a = 0;
		while (a<5) {
			System.out.print(a + " ");
			a = a + 1;
		}
		for (int i = 6; i <=10; i++) {
			System.out.print(i + " ");
		}
		
		
	}

}
